INSERT INTO `berita_detik` (`id`, `judul`, `tanggal`, `isi`, `beritaLink`, `gambarURL`, `sentiment`) VALUES
(1, 'Juliyatmono Dukung Prabowo Capres, Bawa-bawa Trah Gunung Lawu', '0000-00-00', 'Bupati Karanganyar Juliyatmono mendukung Prabowo Subianto capres. Dia kembali menyinggung trah Gunung Lawu.', 'https://www.detik.com/jateng/berita/d-6912729/juliyatmono-dukung-prabowo-capres-bawa-bawa-trah-gunung-lawu', 'https://akcdn.detik.net.id/community/media/visual/2023/09/04/bupati-karanganyar-juliyatmono_43.jpeg?w=250&q=', '{\"score\":0,\"comparative\":0,\"calculation\":[],\"token'),
(2, 'Kadis Karanganyar Penuhi Panggilan Bawaslu soal Dugaan Kampanyekan Bupati', '0000-00-00', 'Kadis di Karanganyar memenuhi panggilan dari Bawaslu Karanganyar. Dia dipanggil untuk dimintai klarifikasi atas videonya yang diduga melakukan kampanye.', 'https://www.detik.com/jateng/berita/d-6842792/kadis-karanganyar-penuhi-panggilan-bawaslu-soal-dugaan-kampanyekan-bupati', 'https://akcdn.detik.net.id/community/media/visual/2023/07/26/kepala-dinas-pariwisata-pemuda-dan-olahraga-disparpora-karanganyar-hari-purnomo-saat-memenuhi-panggilan-dari-bawaslu-karangany_43.jpeg?w=250&q=', '{\"score\":0,\"comparative\":0,\"calculation\":[],\"token'),
(3, 'Diduga Berkampanye, Kepala Dinas di Karanganyar Akan Diperiksa Bawaslu', '0000-00-00', 'Kepala Dinas Pariwisata Pemuda dan Olahraga (Disparpora) Karanganyar Hari Purnomo, diduga melakukan kampanye untuk Bupati Juliyatmono.', 'https://www.detik.com/jateng/berita/d-6841348/diduga-berkampanye-kepala-dinas-di-karanganyar-akan-diperiksa-bawaslu', 'https://akcdn.detik.net.id/community/media/visual/2023/07/25/kadisparpora-karanganyar-hari-purnomo_43.jpeg?w=250&q=', '{\"score\":0,\"comparative\":0,\"calculation\":[],\"token'),
(4, 'Gibran Kaget Juliyatmono Mundur dari Bupati Karanganyar: Kok Cepet Men', '0000-00-00', 'Wali Kota Solo Gibran mengaku kaget dengan kabar pengunduran diri Juliyatmono sebagai Bupati Karanganyar. Namun, Gibran optimistis Yuli bisa duduk di DPR.', 'https://www.detik.com/jateng/berita/d-6809691/gibran-kaget-juliyatmono-mundur-dari-bupati-karanganyar-kok-cepet-men', 'https://akcdn.detik.net.id/community/media/visual/2023/02/06/bupati-karanganyar-juliyatmono-saat-ditemui-wartawan-di-lodji-gandrung-solo-senin-622023_43.jpeg?w=250&q=', '{\"score\":0,\"comparative\":0,\"calculation\":[],\"token'),
(5, 'Maju Nyaleg, Juliyatmono Mundur dari Bupati Karanganyar', '0000-00-00', 'Bupati Karanganyar Juliyatmono telah menyerahkan surat pengunduran diri sebagai kepala daerah ke DPRD Karanganyar. Jekek bakal maju nyaleg.', 'https://www.detik.com/jateng/berita/d-6804272/maju-nyaleg-juliyatmono-mundur-dari-bupati-karanganyar', 'https://akcdn.detik.net.id/community/media/visual/2019/12/26/995b80b1-191a-4872-91ca-c8dc0eabc1fc_43.jpeg?w=250&q=', '{\"score\":0,\"comparative\":0,\"calculation\":[],\"token'),
(6, 'Di Depan Ganjar Bupati Ini Sebut Capres Tak Jauh dari Gunung Lawu, Apa Itu?', '0000-00-00', 'Bupati Karanganyar Juliyatmono menyebut capres 2024 tidak jauh dari Gunung Lawu. Hal tersebut diungkapkan di hadapan Ganjar Pranowo saat Musrenbangwil.', 'https://news.detik.com/pemilu/d-6616349/di-depan-ganjar-bupati-ini-sebut-capres-tak-jauh-dari-gunung-lawu-apa-itu', 'https://akcdn.detik.net.id/community/media/visual/2023/02/06/bupati-karanganyar-juliyatmono-saat-ditemui-wartawan-di-lodji-gandrung-solo-senin-622023_43.jpeg?w=250&q=', '{\"score\":0,\"comparative\":0,\"calculation\":[],\"token'),
(7, 'Bupati Karanganyar Usul Pembangunan Jalan Tol Wonigiri-Pacitan', '0000-00-00', 'Bupati Karanganyar Juliyatmono mengusulkan pembangunan jalan tol Wonogiri-Pacitan. Berikut penuturan lengkap Juliyatmono.', 'https://www.detik.com/jateng/bisnis/d-6565120/bupati-karanganyar-usul-pembangunan-jalan-tol-wonigiri-pacitan', 'https://akcdn.detik.net.id/community/media/visual/2022/02/09/bupati-karanganyar-juliyatmono-rabu-922022_43.jpeg?w=250&q=', '{\"score\":0,\"comparative\":0,\"calculation\":[],\"token'),
(8, 'Ketua Golkar Solo Diberhentikan, Bupati Karanganyar Juliyatmono Jadi Pengganti', '0000-00-00', 'Golkar Jawa Tengah menunjuk Juliyatmono yang merupakan Bupati Karanganyar untuk menjadi pelaksana tugas Ketua Golkar Solo.', 'https://www.detik.com/jateng/berita/d-6561978/ketua-golkar-solo-diberhentikan-bupati-karanganyar-juliyatmono-jadi-pengganti', 'https://akcdn.detik.net.id/community/media/visual/2023/02/10/eks-ketua-dpd-ii-golkar-solo-kusraharjo-kanan_43.jpeg?w=250&q=', '{\"score\":0,\"comparative\":0,\"calculation\":[],\"token'),
(9, 'Didukung Bupati Karanganyar Juliyatmono di Pilgub 2024, Gibran: Saya Seneng', '0000-00-00', 'Ketum Golkar Airlangga Hartarto bertemu Walkot Solo Gibran Rakabuming di Loji Gandrung. Ada pula Bupati Karanganyar, Juliyatmono, di sana. Ini alasannya.', 'https://www.detik.com/jateng/berita/d-6554530/didukung-bupati-karanganyar-juliyatmono-di-pilgub-2024-gibran-saya-seneng', 'https://akcdn.detik.net.id/community/media/visual/2023/02/06/bupati-karanganyar-juliyatmono-saat-ditemui-wartawan-di-lodji-gandrung-solo-senin-622023_43.jpeg?w=250&q=', '{\"score\":0,\"comparative\":0,\"calculation\":[],\"token'),
(10, 'Anang-Ashanty Meriahkan Acara Ngunduh Mantu Bupati Karanganyar', '0000-00-00', 'Penyanyi Anang Hermansyah dan istrinya Ashanty memeriahkan acara ngunduh mantu putra Bupati Karanganyar Juliatmono, Ilyas Akbar dan Putri Rifqi.', 'https://www.detik.com/jateng/berita/d-6503016/anang-ashanty-meriahkan-acara-ngunduh-mantu-bupati-karanganyar', 'https://akcdn.detik.net.id/community/media/visual/2023/01/07/penyanyi-anang-hermansyah-dan-istrinya-ashanty-memeriahkan-acara-ngunduh-mantu-putra-bupati-karanganyar-juliatmono-1_43.jpeg?w=250&q=', '{\"score\":0,\"comparative\":0,\"calculation\":[],\"token');
