(6, 'https://imgcdn.solopos.com/@space/_d-terkini/2022/08/images-4.jpeg', 'Karanganyar', 'Lebih Banyak Berawan, Prakiraan Cuaca Karanganyar Hari Ini Cukup Bersahabat', 'https://soloraya.solopos.com/lebih-banyak-berawan-prakiraan-cuaca-karanganyar-hari-ini-cukup-bersahabat-1798286', '\n                                                         Karanganyar hari ini, Sabtu (18/11/2023) diprakirakan akan dominan berawan. Suhu udara di kisaran 33 derajat Celsius dengan kelembapan 55%.                                                     ', '0000-00-00', 'Kaled Hasby Ashshidiqy', '{\"score\":0,\"comparative\":0,\"calculation\":[],\"tokens\":[\"karanganyar\",\"hari\",\"ini\",\"sabtu\",\"18\",\"11\",\"2023\",\"diprakirakan\",\"akan\",\"dominan\",\"berawan\",\"suhu\",\"udara\",\"di\",\"kisaran\",\"33\",\"derajat\",\"celsius\",\"dengan\",\"kelembapan\",\"55\"],\"words\":[],\"positive\":[],\"negative\":[]}', NULL, NULL),
(7, 'https://imgcdn.solopos.com/@space/_d-terkini/2023/11/Resize_20231117_170501_1694.jpg', 'Karanganyar', 'Sejarah di Balik Monumen Gerakan Sayang Ibu di Alun-alun Karanganyar', 'https://soloraya.solopos.com/sejarah-di-balik-monumen-gerakan-sayang-ibu-di-alun-alun-karanganyar-1797874', '\n                                                         Tak Banyak yang tahu di Alun-alun Karanganyar terdapat Monumen Gerakan Sayang Ibu. Monumen ini ditandatangani oleh Presiden ke-2 RI, Soeharto.                                                     ', '0000-00-00', 'Indah Septiyaning Wardani', '{\"score\":0,\"comparative\":0,\"calculation\":[],\"tokens\":[\"tak\",\"banyak\",\"yang\",\"tahu\",\"di\",\"alun-alun\",\"karanganyar\",\"terdapat\",\"monumen\",\"gerakan\",\"sayang\",\"ibu\",\"monumen\",\"ini\",\"ditandatangani\",\"oleh\",\"presiden\",\"ke-2\",\"ri\",\"soeharto\"],\"words\":[],\"positive\":[],\"negative\":[]}', NULL, NULL),
(8, 'https://imgcdn.solopos.com/@space/_d-terkini/2023/11/Resize_20231117_155341_1559.jpg', 'Karanganyar', 'Amankan Suara Prabowo-Gibran, Gerindra Karanganyar Perkuat Saksi TPS', 'https://soloraya.solopos.com/amankan-suara-prabowo-gibran-gerindra-karanganyar-perkuat-saksi-tps-1797744', '\n                                                         Partai Gerindra Karanganyar akan mengoptimalkan pengawasan para saksi di tempat pemungutan suara (TPS) untuk memastikan tidak ada suara yang hilang untuk pasangan Prabowo-Gibran.                                                     ', '0000-00-00', 'Indah Septiyaning Wardani', '{\"score\":0,\"comparative\":0,\"calculation\":[],\"tokens\":[\"partai\",\"gerindra\",\"karanganyar\",\"akan\",\"mengoptimalkan\",\"pengawasan\",\"para\",\"saksi\",\"di\",\"tempat\",\"pemungutan\",\"suara\",\"tps\",\"untuk\",\"memastikan\",\"tidak\",\"ada\",\"suara\",\"yang\",\"hilang\",\"untuk\",\"pasangan\",\"prabowo-gibran\"],\"words\":[],\"positive\":[],\"negative\":[]}', NULL, NULL),
(9, 'https://imgcdn.solopos.com/@space/_d-terkini/2023/11/Resize_20231117_102152_2528.jpg', 'Karanganyar', 'Cawapres Gibran Ogah Bicara Politik Saat ke Karanganyar', 'https://soloraya.solopos.com/cawapres-gibran-ogah-bicara-politik-saat-ke-karanganyar-1797492', '\n                                                         Wali Kota Solo, Gibran Rakabuming Raka, emoh ditanya soal politik saat berkunjung ke Karanganyar untuk mengikuti acara peletakan batu pertama perumahan ASN.                                                       ', '0000-00-00', 'Indah Septiyaning Wardani', '{\"score\":0,\"comparative\":0,\"calculation\":[],\"tokens\":[\"wali\",\"kota\",\"solo\",\"gibran\",\"rakabuming\",\"raka\",\"emoh\",\"ditanya\",\"soal\",\"politik\",\"saat\",\"berkunjung\",\"ke\",\"karanganyar\",\"untuk\",\"mengikuti\",\"acara\",\"peletakan\",\"batu\",\"pertama\",\"perumahan\",\"asn\"],\"words\":[],\"positive\":[],\"negative\":[]}', NULL, NULL),
(10, 'https://imgcdn.solopos.com/@space/_d-terkini/2023/11/Gibran-Hadiri-Peletakan-Batu-Pertama-Rumah-ASN-Solo-di-Karanganyar.jpeg', 'Karanganyar', 'Wah! PT Taspen Bangun Rumah untuk ASN Solo di Plesungan Karanganyar, DP 0%', 'https://soloraya.solopos.com/wah-pt-taspen-bangun-rumah-untuk-asn-solo-di-plesungan-karanganyar-dp-0-1797450', '\n                                                         Pemkot Solo bekerja sama dengan PT Taspen (Persero) membangun rumah bagi aparatur sipil negara (ASN) di Plesungan, Kecamatan Gondangrejo, Kabupaten Karanganyar.                                                     ', '0000-00-00', 'Indah Septiyaning Wardani', '{\"score\":0,\"comparative\":0,\"calculation\":[],\"tokens\":[\"pemkot\",\"solo\",\"bekerja\",\"sama\",\"dengan\",\"pt\",\"taspen\",\"persero\",\"membangun\",\"rumah\",\"bagi\",\"aparatur\",\"sipil\",\"negara\",\"asn\",\"di\",\"plesungan\",\"kecamatan\",\"gondangrejo\",\"kabupaten\",\"karanganyar\"],\"words\":[],\"positive\":[],\"negative\":[]}', NULL, NULL),
